import React, { useState } from "react";
import styled from "styled-components";
import BACKGROUND_IMAGE from "../../assets/Background.png";
import NMIMS from "../../assets/Nmims.png";
import { Button, Input, InputGroup } from "rsuite";
import MemberIcon from '@rsuite/icons/Member';
import EyeCloseIcon from '@rsuite/icons/EyeClose';
import VisibleIcon from '@rsuite/icons/Visible';

const Login = () => {
    const[hidden, setHidden] = useState(true)

    const Password = ()=>{
        setHidden(!hidden)
    }
  return (
    <Background>
      <Container>
        <Block1>
          <img src={NMIMS} alt="loading" />
        </Block1>
        <Block2>
          <InputGroup className="inputgroup">
            <InputGroup.Addon  className="icon">{<MemberIcon />}</InputGroup.Addon>
            <Input
              type="text"
              placeholder="Username"
            />
          </InputGroup>
          <InputGroup className="inputgroup">
            <InputGroup.Addon onClick={Password} className="icon">{hidden?<EyeCloseIcon/>:<VisibleIcon/>}</InputGroup.Addon>
            <Input
              type={hidden?"password":"text"}
              placeholder="Password"
            />
          </InputGroup>
          <div className="box">

          <Button>
            Login
          </Button>
          </div>
        </Block2>
      </Container>
    </Background>
  );
};

export default Login;
const Background = styled.div`
  width: 100%;
  background-image: url(${BACKGROUND_IMAGE});
  background-size: 100vw 100vh;
  background-repeat: no-repeat;
  object-fit: scale-down;
  z-index: 1;
  height: 100vh;
  box-shadow: inset 0 0 0 2000px rgba(255, 0, 150, 0.3);
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Container = styled.div`
  border: 2px solid white;
  width: 750px;
  height: 350px;
  border-radius: 10px;
  background-color: white;
  box-shadow: 5px 5px 10px 10px white;
  display: flex;
  justify-content: space-around;
  align-items: center;
  padding: 10px;
`;

const Block1 = styled.div`
  height: 80%;
  width: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  img {
    width: 300px;
  }
  border-right: 2px solid red;
`;
const Block2 = styled.div`
  /* border: 2px solid red; */
  height: 70%;
  padding: 20px;
  width: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction:column;
  .inputgroup {
    border: 2px solid red !important;
    height: 50px;
    margin-bottom: 20px;
    input {
      &::placeholder {
        color: red;
      }

    }
    .icon{
        color:red;
        font-size: 22px;
    }

  }
`;
